      return ((h & 1) == 0 ? u : -u) + ((h & 2) == 0 ? v : -v);
    }
    const scale = (n) => { return (1 + n) / 2; }
    return getNoiseFloat(x, y, z);
  }
  linearInterpolation(x, min, max, virtualMin, virtualMax) {
    return ((x - min) / (max - min)) * (virtualMax - virtualMin);
  }
}

