  background: linear-gradient(180deg, #222 0%, #222 49%, #191919 100%);
  background-size: cover;
}
.posts-collapse {
  padding: 20px;
}
.posts-collapse .post-block:first-of-type {
  padding-top: 20px;
}
.posts-expand {
  padding: 40px;
}
.post-body {
  font-size: 1em;
}
.post-body li+li {
  margin-top: 30px;
}
.post-body code {
  color: #ffb531;
}
.site-brand-container {
  padding: 0;
}
.site-meta {
  padding: 0;
}
@media screen and (max-width: 991px) {
  .site-meta {
    padding: 20px 0;
  }
}
.site-subtitle {
  color: rgba(255,255,255,0.35);
  margin: 0px;
  padding: 10px;
  background-color: rgba(0,0,0,0.35);
  user-select: none;
}
@media screen and (max-width: 991px) {
  .site-subtitle {
    padding: 0;
    margin: 10px;
    margin-bottom: 0;
    background-color: transparent;
  }
}
.site-description {
  display: none;
}
.brand {
  font-size: 0px;
  display: block;
  width: 100%;
  position: relative;
  padding-bottom: 50%;
}
@media screen and (max-width: 991px) {
  .brand {
    font-size: 20px;
    padding-bottom: 0px;
  }
}
.brand >*:not(#logo-canvas) {
  display: none;
}
@media screen and (max-width: 991px) {
  .brand >*:not(#logo-canvas) {
    display: block;
  }
}
.brand #logo-canvas {
  width: 100%;
  position: absolute;
  left: 0;
  top: 0;
  background-color: rgba(0,0,0,0.35);
  box-shadow: inset 0px -10px 20px 3px rgba(0,0,0,0.25);
}
@media screen and (max-width: 991px) {
  .brand #logo-canvas {
    display: none;
  }
}

data.css/styl-plugin/grid-post-layout/main:
.sidebar-inner {
  background-color: transparent;
}
.sidebar-inner .site-author-image {
  border: none;
  user-select: none;
}
.site-banner {
  height: 300px;
  position: relative;
  left: 0;
  right: 0;
  margin: 0 auto;
}
@media (min-width: 992px) {
  .site-banner {
    margin-top: 15px;
    width: $content-desktop-large;
    background-color: #222;
    box-shadow: 0 0px 1.4px rgba(0,0,0,0.128), 0 0px 3.6px rgba(0,0,0,0.15), 0 0px 7.4px rgba(0,0,0,0.178), 0 0px 15.3px rgba(0,0,0,0.23), 0 0px 42px rgba(0,0,0,0.32);
  }
}
.headband {
  display: none;
}
.post-meta-item {
  font-size: 12px;
}
h1 {
  font-size: 1.125em;
}
h2 {
  font-size: 1em;
}
h3 {
  font-size: 0.875em;
}
h4 {
  font-size: 0.75em;
}
h5 {
  font-size: 0.625em;
}
h6 {
  font-size: 0.5em;
}
h1 {
  font-size: 1.625em;
}
h2 {
  font-size: 1.5em;
}
h3 {
  font-size: 1.375em;
}
h4 {
  font-size: 1.25em;
}
h5 {
  font-size: 1.125em;
}
h6 {
  font-size: 1em;
}
.posts-expand {
  padding: 40px;
}
.posts-montage {
  padding: 0px;
  margin-left: 15px;
}
.posts-montage h1 {
  font-size: 1.125em;
}
.posts-montage h2 {
  font-size: 1em;
}
.posts-montage h3 {
  font-size: 0.875em;
}
.posts-montage h4 {
  font-size: 0.75em;
}
.posts-montage h5 {
  font-size: 0.625em;
}
.posts-montage h6 {
  font-size: 0.5em;
}
@media screen and (max-width: 992px) {
  .posts-montage {
    margin-left: 0;
  }
}
.posts-montage .post-block {
  user-select: none;
  height: 250px;
  position: relative;
  box-sizing: border-box;
  background-color: #222;
  box-shadow: 0 0px 1.4px rgba(0,0,0,0.128), 0 0px 3.6px rgba(0,0,0,0.15), 0 0px 7.4px rgba(0,0,0,0.178), 0 0px 15.3px rgba(0,0,0,0.23), 0 0px 42px rgba(0,0,0,0.32);
}
.posts-montage .post-block:first-of-type {
  padding-top: 0;
}
.posts-montage .post-block >article {
  position: absolute;
  left: 0;
  right: 0;
  bottom: 0;
  top: 0;
  padding-left: 25px;
  padding-top: 25px;
  margin: auto;
}
.posts-montage .post-block .post-header {
  height: 100%;
}
.posts-montage .post-block .post-header .post-title {
  margin: 0;
  padding-right: 25px;
}
.posts-montage .post-block .post-header .post-title-link {
  border-bottom: none;
}
.posts-montage .post-block .post-header .post-meta-container {
  position: absolute;
  bottom: 0;
  right: 0;
  padding-right: 25px;
  padding-left: 25px;
  padding-bottom: 25px;
}
.posts-montage .post-block .post-header .post-meta {
  justify-content: flex-end;
}
.posts-montage .post-block .post-header .post-meta-item-icon {
  display: none;
}
.posts-montage .post-block .post-header .post-meta-item-text {
  display: none;
}
.posts-montage .post-block .post-body {
  display: none;
}
.posts-montage .post-block .post-footer {
  display: none;
}
.pagination {
  margin: 0;
  padding: 30px 0 20px 0;
  border: none;
}
.pagination .page-number,
.pagination .extend {
  border: none;
}
.header-inner {
  background-color: transparent;
  background-color: #222;
  box-shadow: 0 0px 1.4px rgba(0,0,0,0.128), 0 0px 3.6px rgba(0,0,0,0.15), 0 0px 7.4px rgba(0,0,0,0.178), 0 0px 15.3px rgba(0,0,0,0.23), 0 0px 42px rgba(0,0,0,0.32);
}
.site-brand-container {
  background-color: transparent;
}
@media screen and (max-width: 992px) {
  .site-brand-container {
    background-color: #222;
    border: none;
  }
}
.site-nav {
  margin-top: 15px;
  margin-top: 0;
}
@media screen and (max-width: 992px) {
  .site-nav {
    background-color: #222;
    border: none;
  }
}
.sidebar {
  background-color: #222;
  box-shadow: 0 0px 1.4px rgba(0,0,0,0.128), 0 0px 3.6px rgba(0,0,0,0.15), 0 0px 7.4px rgba(0,0,0,0.178), 0 0px 15.3px rgba(0,0,0,0.23), 0 0px 42px rgba(0,0,0,0.32);
}
.main-inner {
  background-color: transparent;
}
.main-inner.posts-expand {
  background-color: #222;
  background-color: #222;
  box-shadow: 0 0px 1.4px rgba(0,0,0,0.128), 0 0px 3.6px rgba(0,0,0,0.15), 0 0px 7.4px rgba(0,0,0,0.178), 0 0px 15.3px rgba(0,0,0,0.23), 0 0px 42px rgba(0,0,0,0.32);
}
@media screen and (max-width: 992px) {
  .main-inner {
    padding: 20px;
  }
}
.main {
  margin-top: 20px;
}
@media screen and (max-width: 992px) {
  .main {
    margin-top: 0;
  }
}
@media (min-width: $content-desktop-large) {
  .main {
    width: $content-desktop-large;
  }
}
.menu-item a:hover,
.menu-item span.exturl:hover,
.menu-item a.menu-item-active,
.menu-item span.exturl.menu-item-active {
  background-color: transparent;
}
